mapper.select:
SELECT DISTINCT user
FROM t_link_access_logs
WHERE short_uri = #{shortUri}
AND create_time BETWEEN #{startDateTime} AND #{endDateTime}

SELECT user, CASE WHEN MIN(create_time) BETWEEN #{startDateTime} AND #{endDateTime} THEN '新访客' ELSE '老访客' END AS type FROM t_link_access_logs WHERE short_uri = #{shortUri} AND user IN
#{user}
GROUP BY user
